import React, { useEffect } from "react";
import EventItem from "./EventItem";
import OrgSidebar from "./OrgSidebar";

function EventsOrg() {
  const host = "http://localhost:5000";
  let json={length:"0"}
  useEffect(() => {
    // API call
    const Profile = async () => {
      const response = await fetch(`${host}/api/Orgevent/getEvents`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          "auth-token": localStorage.getItem("Orgtoken"),
        },
      });
      json = await response.json();
      console.log(json);
    };
    Profile();
  }, []);

  return (
    <>
      <OrgSidebar/>
      <div
        style={{
          paddingTop: "100px",
          paddingLeft: "30%",
          height: "100%",
        }}
      >
        <div className="container text-center mt-3">
            {json.length===0 && 'No Notes for Display'}
          </div>
          {json.map((event, index) => {
            return <EventItem key={index} event={event}/>;
          })}
        
      </div>
    </>
  );
}

export default EventsOrg;
