import React, { useEffect, useState } from "react";
import EventItemStudent from "./EventItemStudent";
// import EventItemOrg from "./EventItemOrg";
import StudentSidebar from "./StudentSidebar";

function StudentMyEvent() {
  const host = "http://localhost:5000";
  let eventId = [{ date: "", event: "", org: "", student: "", __v: 0, _id: "" }];
  let [eventIds, setEventIds] = useState(eventId);

  let event = [{}];

  let [events, setEvents] = useState(event);

  useEffect(() => {
    // API call
    const Profile = async () => {
      const response = await fetch(
        `${host}/api/Studentevent/getRegisteredEventsId`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "auth-token": localStorage.getItem("Studenttoken"),
          },
        }
      );
      const result = await response.json();
      setEventIds(result);
      console.log(eventIds);
    };
    Profile();

    // if (eventIds.length != 1) {
    //   eventIds.list.map((key, event) => {
    //     getEvents(event);
    //     console.log("1");
    //   });
    // }

  }, []);

  useEffect(() => {
   
    const getEvents = async (event) => {
      console.log(event)
      const response = await fetch(`${host}/api/Studentevent/getEvent`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "auth-token": localStorage.getItem("Studenttoken"),
        },
        body: JSON.stringify({ eventId: JSON.parse(event.event) }),
      });
      const result = await response.json();
      setEvents(events.concat(result));
      // console.log(result);
      console.log(events);
    };
  
  
    eventIds && eventIds.map((event, index)=>{
      getEvents(event)
    })

  },[events])

  return (
    <>
      <StudentSidebar />
      <div
        style={{
          paddingTop: "100px",
          paddingLeft: "30%",
          height: "100%",
        }}
      >
        {eventIds.length === 0 && (
          <div
            className="container text-center mt-3"
            style={{
              width: "100%",
              height: "300px",
              backgroundColor: "#BFEAF5",
              justifyContent: "center",
              marginRight: "5%",
            }}
          >
            <h1>"No Events for Display"</h1>
          </div>
        )}
        
{/* { eventIds &&  eventIds.list.map((event, index) => {
        return getEvents(event);
})} */}
  
        {events && events.map((event, index) => {
          return <EventItemStudent key={index} event={event} />;
        })}
      </div>
    </>
  );
}

export default StudentMyEvent;
