import React, { useEffect, useState } from "react";
import EventItemOrg from "./EventItemOrg";
import StudentSidebar from "./StudentSidebar";

function StudentMyEvent() {
  const host = "http://localhost:5000";
  let eventId = [
    { date: "", event: "", org: "", student: "", __v: 0, _id: "" },
  ];
  let [eventIds, setEventIds] = useState(eventId);

  let event = { _id: "",
  org: "",
  ename: "",
  edate: "",
  etime: "",
  co_ordinator: "",
  mode: "",
  registration_fee: "",
  rules: "",
  address: "",
  phone: "",
  date: ""};

  let [events, setEvents] = useState(event);

  useEffect(() => {
    // API call
    const Profile = async () => {
      const response = await fetch(
        `${host}/api/Studentevent/getRegisteredEventsId`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "auth-token": localStorage.getItem("Studenttoken"),
          },
        }
      );
      const result = await response.json();
      setEventIds(result);
      console.log(result);
      console.log(events);
    };
    Profile();
    
    
    eventIds.map((key, events) => {
        getEvents(events.event);
      })
    
  }, []);

  const getEvents = async (id) => {
    const response = await fetch(`${host}/api/Studentevent/getEvent`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "auth-token": localStorage.getItem("Studenttoken"),
      },
      body: JSON.stringify({ eventId: id }),
    });
    const result = await response.json();
    setEvents(events.concat(result));
    console.log(result);
    console.log(events);
  };

  

  return (
    <>
      <StudentSidebar />
      <div
        style={{
          paddingLeft: "22%",
          height: "100%",
          paddingTop: "100px",
        }}
      >
        {events.length === 0 && (
          <div
            className="container text-center mt-3"
            style={{
              width: "100%",
              height: "300px",
              backgroundColor: "#BFEAF5",
              justifyContent: "center",
              marginRight: "5%",
            }}
          >
            <h1>"No Events for Display"</h1>
          </div>
        )}
        {}
      </div>
    </>
  );
}

export default StudentMyEvent;
