import React from 'react'
import { useEffect} from 'react';
import cf from './CF logo.png'
import { Link, BrowserRouter as Router } from "react-router-dom";



export default function Navbar() {
  
  useEffect(() => {
    let nav=document.querySelector('nav')
  window.addEventListener('scroll',function(){
    if(window.pageYOffset > 100 ){
      nav.classList.add('bg-warning','shadow')
    
    }
    else{
      nav.classList.remove('bg-warning','shadow');
      
    }

  });
  }, []);

  return (
    <>
    <Router>
<nav className="navbar navbar-expand-lg fixed-top">
  <div className="container-fluid">
  
    <Link className="navbar-brand" to="/">
      <img src={cf} alt="CF" width="40" height="40"/>
    </Link>
  
    <Link className="navbar-brand text-body" to="/">CollegeFun</Link>
    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span className="navbar-toggler-icon"></span>
    </button>
    <div className="collapse navbar-collapse" id="navbarSupportedContent">
      <div className='mx-auto'></div>
      <ul className="navbar-nav">
        <li className="nav-item">
          <Link className="nav-link active text-body" aria-current="page" to="/">Home</Link>
        </li>
        <li className="nav-item">
          <Link className="nav-link text-body" to="about">About Us</Link>
        </li>
        <li className="nav-item">
          <Link className="nav-link text-body" to="contact">Contact Us</Link>
        </li>
        
      </ul>
      <button type="button" className="btn btn-outline-danger mx-2">Sign In</button>
      <button type="button" className="btn btn-outline-success mx-2">Sign Up</button>
      </div>
    </div>
  
</nav>
{/* <script>
  let nav=document.querySelector('nav')
  window.addEventListener('scroll',function(){
    if(window.pageYOffset > 100 ){
      nav.classList.add('bg-dark','shadow')
    }
    else{
      nav.classList.remove('bg-dark','shadow');
    }
  });
</script> */}
      </Router>
    </>
  )
};
